namespace Intent.Modules.Common.AI.CodeGeneration.Models;

#nullable enable

/// <summary>
/// Represents a single file change generated by an AI model.
/// </summary>
public class FileChange
{
    /// <summary>
    /// Initializes a new instance of the <see cref="FileChange"/> class.
    /// </summary>
    public FileChange()
    {
    }

    /// <summary>
    /// Initializes a new instance of the <see cref="FileChange"/> class with specified values.
    /// </summary>
    /// <param name="filePath">The path to the file.</param>
    /// <param name="fileExplanation">An explanation of what changes were made to the file.</param>
    /// <param name="content">The complete content of the file.</param>
    public FileChange(string filePath, string fileExplanation, string content)
    {
        FilePath = filePath;
        FileExplanation = fileExplanation;
        Content = content;
    }

    /// <summary>
    /// Gets or sets an explanation of what changes were made to this file.
    /// </summary>
    public string FileExplanation { get; set; } = string.Empty;

    /// <summary>
    /// Gets or sets the path to the file.
    /// </summary>
    public string FilePath { get; set; } = string.Empty;

    /// <summary>
    /// Gets or sets the complete content of the file.
    /// </summary>
    public string Content { get; set; } = string.Empty;

    /// <summary>
    /// Returns a string representation of the file change.
    /// </summary>
    public override string ToString()
    {
        return $"{FilePath} ({FileExplanation})";
    }
}
