/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function checkForDuplicates(elements, element, routeToCheck, verbToCheck, match) {
    let duplicate;
    elements.forEach(e => {
        if (duplicate != null)
            return;
        if (e.id == element.id)
            return;
        if (match(e, element, routeToCheck, verbToCheck)) {
            duplicate = e;
        }
    });
    if (duplicate) {
        return `Duplicate rest route ${element.getName()}(${element.specialization}) with ${duplicate.getName()}(${duplicate.specialization}) - ${routeToCheck}`;
    }
    return null;
}
/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function getOperationPath(operation) {
    let httpSettingsId = "b4581ed2-42ec-4ae2-83dd-dcdd5f0837b6";
    let serviceHttpServiceSettingsId = "c29224ec-d473-4b95-ad4a-ec55c676c4fd";
    let httpSettings = operation.getStereotype(httpSettingsId);
    if (httpSettings == null) {
        return "";
    }
    let operationPath = httpSettings.getProperty("Route").value;
    if (operation.getParent().getStereotype(serviceHttpServiceSettingsId) && operation.getParent().getStereotype(serviceHttpServiceSettingsId).getProperty("Route").value != "") {
        let servicePath = operation.getParent().getStereotype(serviceHttpServiceSettingsId).getProperty("Route").value;
        if (servicePath.toLocaleLowerCase().includes('[controller]')) {
            servicePath = servicePath.replace(/\[controller\]/gi, `[${operation.getParent().getName()}]`);
        }
        return `${servicePath}/${operationPath}`;
    }
    //We don't know how the service name will be transformed so we add [{ServiceName}] to represent the transform
    return `[${operation.getParent().getName()}]/${operationPath}`;
}
/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function matchCommandOrQuery(possibleDuplicate, changingElement, routeToCheck, verbToCheck) {
    let httpSettingsId = "b4581ed2-42ec-4ae2-83dd-dcdd5f0837b6";
    let apiVersionSettingId = "20855f03-c663-4ec6-b106-de06be98f1fe"; //Api Version Setting
    let currentHttpSettings = possibleDuplicate.getStereotype(httpSettingsId);
    if (currentHttpSettings == null) {
        return false;
    }
    let result = routeToCheck == currentHttpSettings.getProperty("Route").value && verbToCheck == currentHttpSettings.getProperty("Verb").value;
    if (result && changingElement.getStereotype(apiVersionSettingId)) {
        let changingVersions = JSON.parse(changingElement.getStereotype(apiVersionSettingId).getProperty("Applicable Versions").value);
        let duplicateVersions = JSON.parse(possibleDuplicate.getStereotype(apiVersionSettingId).getProperty("Applicable Versions").value);
        let intersection = changingVersions.filter((value) => duplicateVersions.includes(value));
        return intersection.length > 0;
    }
    return result;
}
/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function matchOperation(possibleDuplicate, changingElement, routeToCheck, verbToCheck) {
    let httpSettingsId = "b4581ed2-42ec-4ae2-83dd-dcdd5f0837b6";
    let apiVersionSettingId = "20855f03-c663-4ec6-b106-de06be98f1fe"; //Api Version Setting
    let currentHttpSettings = possibleDuplicate.getStereotype(httpSettingsId);
    if (currentHttpSettings == null) {
        return false;
    }
    let result = routeToCheck == getOperationPath(possibleDuplicate) && verbToCheck == currentHttpSettings.getProperty("Verb").value;
    if (result && (changingElement.getStereotype(apiVersionSettingId) || changingElement.getParent().getStereotype(apiVersionSettingId))) {
        let changingVersions;
        let duplicateVersions;
        let changeStereoType;
        let possibleDuplicateStereoType;
        if (changingElement.getStereotype(apiVersionSettingId)) {
            changeStereoType = changingElement.getStereotype(apiVersionSettingId);
        }
        else if (changingElement.getParent().getStereotype(apiVersionSettingId)) {
            changeStereoType = changingElement.getParent().getStereotype(apiVersionSettingId);
        }
        else {
            return result;
        }
        if (possibleDuplicate.getStereotype(apiVersionSettingId)) {
            possibleDuplicateStereoType = possibleDuplicate.getStereotype(apiVersionSettingId);
        }
        else if (possibleDuplicate.getParent().getStereotype(apiVersionSettingId)) {
            possibleDuplicateStereoType = possibleDuplicate.getParent().getStereotype(apiVersionSettingId);
        }
        else {
            return result;
        }
        changingVersions = JSON.parse(changeStereoType.getProperty("Applicable Versions").value);
        duplicateVersions = JSON.parse(possibleDuplicateStereoType.getProperty("Applicable Versions").value);
        let intersection = changingVersions.filter(value => duplicateVersions.includes(value));
        return intersection.length > 0;
    }
    return result;
}
/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function matchParameters(element, routeToCheck) {
    let httpSettingsId = "b4581ed2-42ec-4ae2-83dd-dcdd5f0837b6";
    let apiVersionSettingId = "20855f03-c663-4ec6-b106-de06be98f1fe"; // Api Version Setting
    // Extract all parameters from the route
    let routeParameters = routeToCheck.match(/{([^}]*)}/g) || [];
    let elementType = element.specialization; // Assuming this is how you identify the element type
    let elementChildren;
    if (elementType === "Command" || elementType === "Query") {
        // For Commands and Queries, consider children that are properties
        elementChildren = element.getChildren("DTO-Field");
    }
    else if (elementType === "Operation") {
        // For Operations, consider children that are parameters
        elementChildren = element.getChildren("Parameter");
    }
    else {
        return null; // Return no message if element type is unrecognized
    }
    // Normalize route parameters by trimming braces and converting to lower case for case-insensitive comparison
    let normalizedRouteParameters = routeParameters.map(param => param.replace(/[{}]/g, "").toLowerCase());
    console.log(`Normalized route parameters found: ${normalizedRouteParameters.join(", ")}`);
    // Collect all names of properties/parameters for the element and convert them to lower case
    let elementNames = elementChildren.map(child => child.getName().toLowerCase());
    console.log(`Element properties/parameters names found: ${elementNames.join(", ")}`);
    // Ensure the comparison against element names is case-insensitive
    let unmatchedParameters = normalizedRouteParameters.filter(param => !elementNames.includes(param.toLowerCase()));
    if (unmatchedParameters.length > 0) {
        console.log(`Unmatched parameters found: ${unmatchedParameters.join(", ")}`);
        // Concatenate the unmatched parameters to the error message
        return `Route mismatch: some route parameters do not match element's properties/parameters. Unmatched parameters: ${unmatchedParameters.join(", ")}`;
    }
    console.log("No unmatched parameters found. Validation passed.");
    return null; // Return no message if all validations pass
}
