                field.typeReference.setType(key.typeId);
                if (((_a = key.mapPath) !== null && _a !== void 0 ? _a : []).length > 0) {
                    field.setMapping(key.mapPath);
                }
            }
        }
    }
    static getParameterFormat(str) {
        return toCamelCase(str);
    }
