                field.typeReference.setType(entry.typeId);
                field.typeReference.setIsNullable(entry.isNullable);
                field.typeReference.setIsCollection(entry.isCollection);
                field.setMapping(entry.mapPath);
                dtoUpdated = true;
            }
        
