const getTypeReferenceCssClass = (typeReference) => {
    if (typeReference.typeId == null || typeReference.getType()?.specialization == "Type-Definition") {
        return 'keyword';
    } else {
        return 'typeref';
    }
}
const result = [
    { text: 'in : ', cssClass: 'annotation' }, 
    { text: `${ name }${ genericTypes }`, cssClass: 'muted' },
    { text: ': ', cssClass: 'annotation' },
    { text: typeReference.display, cssClass: getTypeReferenceCssClass(typeReference), targetId: typeReference.typeId }
];
return result;

/// <reference path="../../../typings/elementmacro.context.api.d.ts" />
function validateDuplicateOperation(operation) {
    var possibleDuplicates = findPeerOperations(operation);
    let operationSignature = calculateSignature(operation);
    let duplicate;
    possibleDuplicates.forEach(possibleDuplicate => {
        if (duplicate != null)
            return;
        if (operationSignature == calculateSignature(possibleDuplicate)) {
            duplicate = possibleDuplicate;
        }
    });
    if (duplicate) {
        return `Duplicate operation ${operation.getName()} - ${operationSignature}`;
    }
    return "";
}
function calculateSignature(operation) {
    let result = `${operation.getName()}(`;
    let params = operation.getChildren("Parameter");
    result += params.map((p) => p.typeReference.getType().getName()).join(', ');
    result += ")";
    return result;
}
function findPeerOperations(operation) {
    return operation.getParent().getChildren("Operation").filter(x => x.id != operation.id);
}
/// <reference path="../common/operation-validation.ts" />
/**
 * Used by Intent.Modules\Modules\Intent.Modules.Modelers.Services
 *
 * Source code here:
 * https://github.com/IntentArchitect/Intent.Modules/blob/master/DesignerMacros/src/services-operation-validation/operation-parameter/operation-validation.ts
 */
function validateOperationParameter(element) {
    let operation = element.getParent();
    return validateDuplicateOperation(operation);
}
